    public static TreasureInstructorUpgradeCostsDto getUpgradeCost(int level) {
        BigInteger goldCost;
        long mushroomCost = 0;

        if (level < 1) {
            throw new IllegalArgumentException("Level must be at least 1");
        }

        if (level == 1) goldCost = BigInteger.valueOf(5);
        else if (level == 2) goldCost = BigInteger.valueOf(10);
        else if (level == 3) goldCost = BigInteger.valueOf(25);
        else if (level == 4) goldCost = BigInteger.valueOf(100);
        else if (level == 5) goldCost = BigInteger.valueOf(250);
        else if (level == 6) { goldCost =  BigInteger.valueOf(500); mushroomCost = 2; }
        else if (level == 7) { goldCost =  BigInteger.valueOf(1000); mushroomCost = 4; }
        else if (level == 8) { goldCost =  BigInteger.valueOf(1500); mushroomCost = 6; }
        else if (level == 9) { goldCost =  BigInteger.valueOf(2000); mushroomCost = 8; }
        else if (level == 10) { goldCost =  BigInteger.valueOf(2500); mushroomCost = 10; }
        else if (level <= 25) { goldCost =  BigInteger.valueOf(5000); mushroomCost = 10; }
        else {
            throw new IllegalArgumentException("Level must be between 1 and 24");
        }

        return new TreasureInstructorUpgradeCostsDto(goldCost, mushroomCost);
    }
